import React, { useState } from 'react';
import { View, Alert } from 'react-native'; // Alert use kiya (instead of alert())
import { styles } from '../../styles/styles';
import { TextInput, Button, Text } from '../ui'; 

/**
 * Custom/SearchAndAdd
 * Props: currentItems, setItems, searchTerm, setSearchTerm (Task D: State Lifting)
 */
export default function SearchAndAdd({ currentItems, setItems, searchTerm, setSearchTerm }) {
  // Local state for the phrase being typed for addition
  const [newPhrase, setNewPhrase] = useState('');
  
  // --- Add Logic ---
  const handleAddZikr = () => {
    const phrase = newPhrase.trim(); 
    
    if (!phrase) {
      Alert.alert('Error', 'Please enter a phrase to add.'); // Use Alert for RN compatibility
      return;
    }

    // Check for uniqueness (case-insensitive)
    const isDuplicate = currentItems.some(item => 
      item.phrase.toLowerCase() === phrase.toLowerCase()
    );

    if (isDuplicate) {
      Alert.alert('Error', `'${phrase}' is already in the list.`);
      setNewPhrase('');
      return;
    }

    // Add new item with unique ID and count: 0 (Immutable Update)
    const newItem = {
      id: Date.now().toString(), 
      phrase: phrase,
      count: 0,
    };

    // Update state by appending the new item
    setItems([...currentItems, newItem]);

    // Clear the input field
    setNewPhrase('');
  };
  // -------------------------

  return (
    <View style={styles.section}>
      <Text style={styles.sectionTitle}>Add New Zikr</Text>
      {/* Add Box */}
      <View style={styles.inputRow}>
        <TextInput 
          style={styles.textInput}
          placeholder="New Zikr Phrase"
          value={newPhrase}
          onChangeText={setNewPhrase}
        />
        <Button 
          style={[styles.button, styles.addButton]} 
          // FIX 3: onPress mein function reference pass karein, call nahi: onPress={handleAddZikr}
          onPress={handleAddZikr} // ✅ Corrected call
        >
          <Text style={styles.addButtonText}>Add</Text>
        </Button>
      </View>

      <Text style={styles.sectionTitle}>Search</Text>
      {/* Search Box */}
      <TextInput 
        style={styles.textInput}
        placeholder="Search for Zikr (case-insensitive)"
        value={searchTerm} // ✅ FIX 4: Use 'searchTerm' from props
        onChangeText={setSearchTerm} // ✅ FIX 5: Use 'setSearchTerm' from props
      />
    </View>
  );
}
