//import React from 'react';
import React, { useState } from 'react';
import { View } from 'react-native';
import { styles } from '../../styles/styles';
import { TextInput, Button, Text } from '../ui'; // Import from UI layer

/**
 * Custom/SearchAndAdd
 * Students implement:
 *  - a text box to add a new zikr (phrase only, count starts at 0)
 *  - a search box to filter existing azkaar by phrase
 *  - use only components from 'ui' for inputs and buttons
 *  - lifting state up if needed
 */
export default function SearchAndAdd({ currentItems, setItems, searchTerm, setSearchTerm }) {
  // State for the new phrase to be added
  const [newPhrase, setNewPhrase] = useState('');
  /// Remove: const [searchText, setSearchText] = useState(''); 
  /// Ab hum App.js ki state use karenge: searchTerm aur setSearchTerm
  // State for the search text
  //const [searchText, setSearchText] = useState('');
  //task d
  // --- Add Logic ---
  const handleAddZikr = () => {
    // Trim whitespace and convert to lowercase for case-insensitive comparison
    const phrase = newPhrase.trim(); 
    
    if (!phrase) {
      alert('Please enter a phrase to add.');
      return;
    }

    // Check for uniqueness (case-insensitive)
    const isDuplicate = currentItems.some(item => 
      item.phrase.toLowerCase() === phrase.toLowerCase()
    );

    if (isDuplicate) {
      alert(`'${phrase}' is already in the list.`);
      setNewPhrase('');
      return;
    }

    // Add new item (Immutable Update)
    const newItem = {
      // Simple timestamp-based unique ID (or use a library like uuid)
      id: Date.now().toString(), 
      phrase: phrase,
      count: 0,
    };

    // Update state by appending the new item
    setItems([...currentItems, newItem]);

    // Clear the input field
    setNewPhrase('');
  };
  return (
    <View style={styles.section}>
      {/* TODO: Implement search and add UI here using ui/TextInput and ui/Button */}
    <Text style={styles.sectionTitle}>Add New Zikr</Text>
      <View style={styles.inputRow}>
        <TextInput 
          style={styles.textInput}
          placeholder="New Zikr Phrase"
          value={newPhrase}
          onChangeText={setNewPhrase}
        />
        <Button 
          style={styles.addButton} 
          onPress={() => { handleAddZikr}}
        >
          Add
        </Button>
      </View>

      <Text style={styles.sectionTitle}>Search</Text>
      <TextInput 
        style={styles.textInput}
        placeholder="Search for Zikr"
        value={searchText}
        onChangeText={setSearchText}
      />
      {/* Search Button ki zaroorat nahi, filtering onChangeText se hi ho jayegi */}
    </View>
  );
}
