import React from 'react';
import { View, FlatList } from 'react-native';
import { Text, Button } from '../ui'; // Task A & C: Text and Button imported from UI layer
import { styles } from '../../styles/styles';

/**
 * Custom/TasbihList
 * Renders a FlatList of azkaar with their counts.
 * Props: items (array of azkaar), setItems (state setter function)
 */
export default function TasbihList({ items, setItems }) {
  
  // --- Core Logic: Immutable Update ---
  const updateCount = (id, delta) => {
    setItems(currentItems => { 
      return currentItems.map(item => {
        if (item.id === id) {
          const newCount = item.count + delta;
          // Count ko 0 se neeche nahi jaane dena
          const finalCount = Math.max(0, newCount); 
          
          return {
            ...item, 
            count: finalCount, // Nayi count set karo (Immutable)
          };
        }
        return item;
      });
    });
  };

  const increment = (id) => updateCount(id, 1);
  const decrement = (id) => updateCount(id, -1);
  // ------------------------------------

  const renderItem = ({ item }) => (
    <View style={styles.itemRow}>
      <Text style={styles.itemName}>{item.phrase}</Text>
      <View style={styles.counterContainer}>
        {/* FIX 1: onPress handler mein decrement function ko call karein */}
        <Button 
          style={[styles.button, styles.decrementButton]} 
          onPress={() => decrement(item.id)} // ✅ Corrected call
        >
          -
        </Button>
        <Text style={styles.counter}>{item.count}</Text>
        {/* FIX 2: onPress handler mein increment function ko call karein */}
        <Button 
          style={[styles.button, styles.incrementButton]} 
          onPress={() => increment(item.id)} // ✅ Corrected call
        >
          +
        </Button>
      </View>
    </View>
  );

  return (
    <View style={styles.section}>
      <Text style={styles.sectionTitle}>Tasbih Counter</Text>
      <FlatList
        data={items} 
        keyExtractor={(it) => it.id}
        renderItem={renderItem}
      />
    </View>
  );
}
