import React, { useState } from 'react';
import { View, FlatList } from 'react-native'; // You may switch Text to ui/Text later
import { Text , Button} from '../ui';
import { styles } from '../../styles/styles';
import { initialAzkaar } from '../../data/azkaar';

/**
 * Custom/TasbihList
 * Renders a FlatList of azkaar with their counts.
 * NOTE: Increment/Decrement buttons are intentionally NOT implemented.
 * Students will add + and - controls using UI/Button and update state accordingly.
 */
export default function TasbihList({ items, setItems }) {
  //const [items, setItems] = useState(initialAzkaar);
  // --- Core Logic: Immutable Update ---
  const updateCount = (id, delta) => {
    setItems(currentItems => {
      // 1. Map ke zariye naya array banao
      return currentItems.map(item => {
        // 2. Agar item ki ID match karti hai
        if (item.id === id) {
          // 3. Naya object return karo, count ko badalkar (Immutable)
          const newCount = item.count + delta;
          // Count ko 0 se neeche nahi jaane dena
          const finalCount = Math.max(0, newCount); 
          
          return {
            ...item, // Purane properties copy karo
            count: finalCount, // Nayi count set karo
          };
        }
        // Agar ID match nahi karti, toh item ko waise hi return karo
        return item;
      });
    });
  };

  const increment = (id) => updateCount(id, 1);
  const decrement = (id) => updateCount(id, -1);
  // ------------------------------------

  // HINT ONLY (do not complete): you will need handlers like increment(id) / decrement(id)

  const renderItem = ({ item }) => (
    <View style={styles.itemRow}>
      <Text style={styles.itemName}>{item.phrase}</Text>
      
      <View style={styles.counterContainer}> {/* Use a View for layout */}
        <Button style={styles.decrementButton} onPress={() => { /* Decrement Logic */ }}>
          -
        </Button>
      <Text style={styles.counter}>{item.count}</Text>
      {/* TODO: Add increment/decrement buttons here using ui/Button */}
      <Button style={styles.incrementButton} onPress={() => { /* Increment Logic */ }}>
          +
        </Button>
      </View>
    </View>
  );

  return (
    <View style={styles.section}>
      <Text style={styles.sectionTitle}>Tasbih Counter</Text>
      <FlatList
        data={items}
        keyExtractor={(it) => it.id}
        renderItem={renderItem}
      />
    </View>
  );
}
