import { StyleSheet } from 'react-native';

/**
 * ðŸ‘‰ Students: create all your styles here.
 * Start with: container, headerText, sectionTitle, itemRow, itemName, counter, input, button, etc.
 * Keep style names semantic.
 */
export const styles = StyleSheet.create({
  // e.g., container: { },
  // leave empty for now; using undefined styles is acceptable.
  container: { 
    flexGrow: 1, // ScrollView ke liye zaroori
    padding: 20,
    backgroundColor: '#f8f8f8',
  },
  section: {
    marginVertical: 15,
    padding: 15,
    backgroundColor: '#fff',
    borderRadius: 8,
    shadowColor: '#000',
    shadowOffset: { width: 0, height: 1 },
    shadowOpacity: 0.1,
    shadowRadius: 3,
    elevation: 2,
  },
  header: {
    marginBottom: 20,
    alignItems: 'center',
  },
  headerText: { 
    fontSize: 18,
    fontWeight: 'bold',
    color: '#333',
  },
  sectionTitle: {
    fontSize: 16,
    fontWeight: 'bold',
    marginBottom: 10,
    color: '#007aff',
  },

  // Tasbih List Styles
  itemRow: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    paddingVertical: 10,
    borderBottomWidth: 1,
    borderBottomColor: '#eee',
  },
  itemName: {
    flex: 2,
    fontSize: 16,
    color: '#333',
  },
  counterContainer: {
    flex: 1,
    flexDirection: 'row',
    justifyContent: 'flex-end',
    alignItems: 'center',
  },
  counter: {
    fontSize: 16,
    fontWeight: 'bold',
    width: 40, // Fixed width for alignment
    textAlign: 'center',
    marginHorizontal: 8,
  },
  
  // Button Styles (General)
  button: { // Button.js mein 'style' prop ka naam generic hai
    padding: 8,
    borderRadius: 5,
    minWidth: 40,
    alignItems: 'center',
    justifyContent: 'center',
  },
  incrementButton: {
    backgroundColor: '#4cd964', // Green
  },
  decrementButton: {
    backgroundColor: '#ff3b30', // Red
  },

  // Search & Add Styles
  inputRow: {
    flexDirection: 'row',
    marginBottom: 10,
    alignItems: 'center',
  },
  textInput: {
    flex: 1,
    borderWidth: 1,
    borderColor: '#ccc',
    padding: 10,
    borderRadius: 5,
    marginRight: 10,
    backgroundColor: '#fff',
  },
  addButton: {
    backgroundColor: '#007aff', // Blue
    paddingVertical: 10,
    paddingHorizontal: 15,
    borderRadius: 5,
    alignSelf: 'stretch',
  },
});
